test("writes an int8", (t) => {
  const buf = new PolusBuffer();

  buf.write8(127);
  buf.write8(-16);
  buf.write8(86);
  buf.write8(-128);

  t.is("7ff05680", buf.buf.toString("hex"));
  t.is(4, buf.length);
});

